import React, { useEffect } from 'react';
import './Technologies.css'; // For custom CSS styles if needed

const technologies = [
  {
    name: 'HTML',
    icon: 'ri-html5-line',
   
  },
  {
    name: 'CSS',
    icon: 'ri-css3-line',
    desc: 'Style sheet language',
    tooltip: 'Cascading Style Sheets',
  },
  {
    name: 'JavaScript',
    icon: 'ri-javascript-line',
    desc: 'Programming language for web',
    tooltip: 'Client-side scripting language',
  },
  {
    name: 'TypeScript',
    icon: 'ri-typescript-line',
    desc: 'Typed superset of JavaScript',
    tooltip: 'Developed by Microsoft',
  },
  {
    name: 'React.js',
    icon: 'ri-reactjs-line',
    desc: 'JavaScript library for UI',
    tooltip: 'Developed by Facebook',
  },
  {
    name: 'Next.js',
    icon: 'ri-nextjs-line',
    desc: 'React framework for production',
    tooltip: 'Built on top of React',
  },
  {
    name: 'Tailwind CSS',
    icon: 'ri-tailwind-css-line',
    desc: 'Utility-first CSS framework',
    tooltip: 'Highly customizable',
  },
  {
    name: 'C Programming',
    icon: 'ri-code-s-slash-line',
    desc: 'General-purpose language',
    tooltip: 'Procedural programming language',
  },
  {
    name: 'C++',
    icon: 'ri-code-box-line',
    desc: 'Extension of C language',
    tooltip: 'Object-oriented programming',
  },
  {
    name: 'Python',
    icon: 'ri-python-line',
    desc: 'High-level programming language',
    tooltip: 'Used in AI, ML, and data science',
  },
  {
    name: 'Django',
    icon: 'ri-django-line',
    desc: 'Python web framework',
    tooltip: 'High-level Python framework',
  },
  {
    name: 'MySQL',
    icon: 'ri-database-2-line',
    desc: 'Relational database system',
    tooltip: 'Open-source database',
  },
  {
    name: 'Git',
    icon: 'ri-git-branch-line',
    desc: 'Version control system',
    tooltip: 'Distributed version control',
  },
  {
    name: 'Firebase',
    icon: 'ri-fire-line',
    desc: 'App development platform',
    tooltip: "Google's mobile platform",
  },
  {
    name: 'Supabase',
    icon: 'ri-database-line',
    desc: 'Open source Firebase alternative',
    tooltip: 'Built on PostgreSQL',
  },
  {
    name: 'Node.js',
    icon: 'ri-nodejs-line',
    desc: 'JavaScript runtime environment',
    tooltip: 'Server-side JavaScript',
  },
  {
    name: 'Figma',
    icon: 'ri-figma-line',
    desc: 'UI/UX design tool',
    tooltip: 'Collaborative design platform',
  },
];

const Technologies = () => {
  useEffect(() => {
    const cards = document.querySelectorAll('.tech-card');
    cards.forEach((card) => {
      card.addEventListener('mousemove', (e) => {
        const rect = card.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;
        card.style.setProperty('--mouse-x', `${x}px`);
        card.style.setProperty('--mouse-y', `${y}px`);
      });

      card.addEventListener('mouseleave', () => {
        const tooltip = card.querySelector('.tooltip');
        if (tooltip) {
          tooltip.style.opacity = '0';
          tooltip.style.transform = 'translateY(10px)';
        }
      });
    });
  }, []);

  return (
    <div className="min-h-screen p-8 flex flex-col items-center justify-center bg-[#0A192F]">
      <div className="container mx-auto max-w-6xl">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 justify-center items-center">
          {technologies.map((tech, index) => (
            <div
              key={index}
              className="tech-card relative p-6 rounded flex items-center bg-[#112240] border border-[#233554] shadow-lg mx-auto"
            >
              <div className="tech-icon mr-4 text-[#64FFDA] flex items-center justify-center">
                <i className={`${tech.icon} ri-2x`} />
              </div>
              <div>
                <h3 className="text-xl font-semibold text-[#64FFDA]">{tech.name}</h3>
                <p className="text-gray-300 text-sm mt-1">{tech.desc}</p>
              </div>
              <div className="tooltip">{tech.tooltip}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Technologies;
