import React, { useEffect } from 'react';
import './Technologies.css'; // For custom CSS styles if needed

const technologies = [
  {
    name: 'HTML',
    icon: 'ri-html5-line',
    desc: 'Markup language for creating web pages.',
  },
  {
    name: 'CSS',
    icon: 'ri-css3-line',
    desc: 'Stylesheet language for designing web pages.',
  },
  {
    name: 'JavaScript',
    icon: 'ri-javascript-line',
    desc: 'Programming language for web development.',
  },
  {
    name: 'React.js',
    icon: 'ri-reactjs-line',
    desc: 'JavaScript library for building user interfaces.',
  },
  {
    name: 'Tailwind CSS',
    icon: 'ri-tailwind-css-line',
    desc: 'Utility-first CSS framework for rapid UI development.',
  },
  {
    name: 'MySQL',
    icon: 'ri-database-2-line',
    desc: 'Relational database management system.',
  },
  {
    name: 'Git',
    icon: 'ri-git-branch-line',
    desc: 'Version control system for tracking code changes.',
  },
  {
    name: 'GitHub',
    icon: 'ri-github-line',
    desc: 'Platform for hosting and collaborating on code.',
  },
  {
    name: 'Node.js',
    icon: 'ri-server-line',
    desc: 'JavaScript runtime for server-side development.',
  },
  {
    name: 'Express.js',
    icon: 'ri-server-line', // No official Remix icon, using server icon as fallback
    desc: 'Web framework for Node.js.',
  },
  {
    name: 'MongoDB',
    icon: 'ri-database-line', // No official Remix icon, using database icon as fallback
    desc: 'NoSQL database for modern applications.',
  },
  {
    name: 'Java',
    icon: 'ri-java-line', // If not available, use 'ri-code-s-slash-line' as fallback
    desc: 'General-purpose programming language.',
  },
];

const Technologies = () => {
  useEffect(() => {
    const cards = document.querySelectorAll('.tech-card');
    cards.forEach((card) => {
      card.addEventListener('mousemove', (e) => {
        const rect = card.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;
        card.style.setProperty('--mouse-x', `${x}px`);
        card.style.setProperty('--mouse-y', `${y}px`);
      });

      card.addEventListener('mouseleave', () => {
        const tooltip = card.querySelector('.tooltip');
        if (tooltip) {
          tooltip.style.opacity = '0';
          tooltip.style.transform = 'translateY(10px)';
        }
      });
    });
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-[#0A192F] ">
      <div className="container mx-auto max-w-6xl">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3 justify-center items-center">
          {technologies.map((tech, index) => (
            <div
              key={index}
              className="tech-card relative p-6 rounded flex items-center bg-[#112240] border border-[#233554] shadow-lg mx-auto"
            >
              <div className="tech-icon mr-4 text-[#64FFDA] flex items-center justify-center">
                <i className={`${tech.icon} ri-2x`} />
              </div>
              <div>
                <h3 className="text-xl font-semibold text-[#64FFDA]">{tech.name}</h3>
                <p className="text-gray-300 text-sm mt-1">{tech.desc}</p>
              </div>
          
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Technologies;
